    #define TOM_MYSQL__FUN__BACKTICKS(input) \
            std::string backticks(const std::string& inStr) {           \
              std::string flag = "DATE_FORMAT";                         \
              if(inStr.size() > flag.size()) {                          \
                if(inStr.substr(0, flag.size()).compare(flag) == 0) {   \
                  return inStr;                                         \
                }                                                       \
              }                                                         \
              return ("`" + inStr + "`");                               \
            }                                                        


